mapper.select:
select * from users left outer join skill on 
users.user_id = skill.user_id where
users.user_id=#{user_id}

mapper.update:
update study_group
set accept_status = '승인완료'
where user_id = #{user_id} and s_seq = #{s_seq}

update study_group
set accept_status = '승인거절'
where user_id = #{user_id} and s_seq = #{s_seq}

update study_group
set accept_status = '승인대기중'
where user_id = #{user_id} and s_seq = #{s_seq}
