mapper.select:
select * from lecture_board left outer join language_category on lecture_board.lan_seq = language_category.lan_seq left outer join site_category on lecture_board.site_seq = site_category.site_seq left outer join price_category on lecture_board.p_seq = price_category.p_seq
where lecture_board.l_title like CONCAT('%',
#{item} , '%'
where lecture_board.l_address like CONCAT('%', #{cri.keyword}, '%')
where lecture_board.l_writer like CONCAT('%', #{cri.keyword}, '%')
where (l_title like CONCAT('%', #{keyword}, '%') or lecture_board.l_address like CONCAT('%', #{cri.keyword}, '%'))
where (l_title like CONCAT('%', #{keyword}, '%') or lecture_board.l_address like CONCAT('%', #{cri.keyword}, '%') or lecture_board.l_writer like CONCAT('%', #{cri.keyword}, '%'))
and lecture_board.p_seq = #{price}
and lecture_board.cate_level = #{level}
and lecture_board.lan_seq in
#{item}
and lecture_board.site_seq = #{site}
group by l_key ORDER BY L_SEQ DESC LIMIT #{pageStart}, #{perPageNum}

select * from lecture_board left outer join language_category on lecture_board.lan_seq = language_category.lan_seq left outer join site_category on lecture_board.site_seq = site_category.site_seq left outer join price_category on lecture_board.p_seq = price_category.p_seq
where lecture_board.l_title like CONCAT('%',#{cri.keyword},'%')
where lecture_board.l_address like CONCAT('%', #{cri.keyword}, '%')
where lecture_board.l_writer like CONCAT('%', #{cri.keyword}, '%')
where (l_title like CONCAT('%', #{keyword}, '%') or lecture_board.l_address like CONCAT('%', #{cri.keyword}, '%'))
where (l_title like CONCAT('%', #{keyword}, '%') or lecture_board.l_address like CONCAT('%', #{cri.keyword}, '%') or lecture_board.l_writer like CONCAT('%', #{cri.keyword}, '%'))
and lecture_board.p_seq = #{price}
and lecture_board.cate_level = #{level}
and lecture_board.lan_seq in
#{item}
and lecture_board.site_seq = #{site}
group by l_key ORDER BY L_SEQ DESC
